const turtleImage = "data:image/png;base64,";
const tRexImage = "data:image/png;base64,"
const octopusImage = "data:image/png;base64,";
const lightningImage = "data:image/png;base64,";

$(document).ready(function() {
  const wordsArray = [ "Lorem", "ipsum", "dolor", "sit", "amet", "consectetur", "adipisicing", "elit", "sed", "do", "eiusmod", "tempor", "incididunt", "ut", "labore", "et", "dolore", "magna", "aliqua", "Ut", "enim", "ad", "minim", "veniam", "quis", "nostrud", "exercitation", "ullamco", "laboris", "nisi", "ut", "aliquip", "ex", "ea", "commodo", "consequat", "Duis", "aute", "irure", "dolor", "in", "reprehenderit", "in", "voluptate", "velit", "esse", "cillum", "dolore", "eu", "fugiat", "nulla", "pariatur", "Excepteur", "sint", "occaecat", "cupidatat", "non", "proident", "sunt", "in", "culpa", "qui", "officia", "deserunt", "mollit", "anim", "id", "est", "laborum", "Donec", "elementum", "ligula", "eu", "sapien", "consequat", "eleifend" ];

  const inputs = document.querySelector('.input');
  const wordsContainer = document.querySelector('.wordsContainer')
  let clickSum = 0;
  let wordsSum = 0;
  let currentWord = 0;
  let timeStart = true;
  const time = 30; // seconds

  function displayWords(array) {
    wordsContainer.innerHTML = array.map( (value) => {return `<span class='word'>${value}</span>`}).join('');
  }

  function addActive() {
    const spans = document.querySelectorAll('.word');
    spans.item(currentWord).classList.add('active');
  }

  function removeActive() {
    document.querySelector('.active').classList.remove('active');
  }

  function onFlyCheck() {
    const span = document.querySelector('.active');
    const wordSpell = span.textContent.split('');
    const inputSpell = this.value.split('');
    const output = wordSpell.map( (value) => `<span>${value}</span>`);

    if (inputSpell.length > wordSpell.length) return;

    inputSpell.map( (value, index) => {
      if (wordSpell[index] === value) output[index] = `<span class="correct">${wordSpell[index]}</span>`;
      else output[index] = `<span class="incorrect">${wordSpell[index]}</span>`
    });
    span.innerHTML = output.join('');
  }

  function commitWord(e) {
    if (e.keyCode === 32 || e.keyCode === 13) {
      checkWord(this.value.trim());
      this.value = "";
    }
  }

  function checkWord(word) {
    const active = document.querySelector('.active');
    active.innerHTML = wordsArray[currentWord];

    if (word === wordsArray[currentWord]) {
      active.classList.add('correct');
      wordsSum++;
      clickSum += (wordsArray[currentWord].length);
    } else {
      active.classList.add('incorrect')
    }

    currentWord++;
    removeActive();
    addActive();
    scrollWords();
  }

  function startTime() {
    if (timeStart) countDown();
    timeStart = false
  }

  $("#keeptyping").hide(1);


  function countDown() {
    setTimeout( endGame, time*1000);
    $("#begintyping").fadeOut(50);
    $("#keeptyping").fadeIn(200);
    $(".livewpm").fadeOut(70000);
  }


  function endGame() {
    const cpm = clickSum / time * 60;
    const cps = cpm / 60;
    const wpm = Math.round(cps / 5 * 60);
    const score = document.querySelector('.score');
    inputs.disabled = true;

    if (wpm < 50){
      score.innerHTML = `You typed...<br>
      <p>${wpm} WPM. You're a turtle! </p>
      <img src="${turtleImage}" />
      <p>${wordsSum} correct word(s). </p>
      <p> Refresh page to start again </p>`
    } else if (wpm < 60) {
      score.innerHTML = `Congratulations! Your score is ... <br>
      <p>${wpm} WPM. You're a T-Rex! </p>
      <img src="${tRexImage}" />
      <p>${wordsSum} correct word(s). </p>
      <p> Refresh page to start again </p>`
    } else if (wpm < 70) {
      score.innerHTML = `Congratulations! Your score is ... <br>
      <p>${wpm} WPM. You're an octopus! </p>
      <img src="${octopusImage}" />
      <p>${wordsSum} correct word(s). </p>
      <p> Refresh page to start again </p>`
    } else if (wpm > 70) {
      score.innerHTML = `<span id="wow">WOW!!!</span><br> YOU TYPED ${wpm} WPM!<br>
      <p> You're typing like a lightning! </p>
      <img src="${lightningImage}" />
      <p>${wordsSum} correct word(s). </p>
      <p> Refresh page to start again </p>`
    }
  }

  function shuffle(array) {
    for (let i = array.length; i; i--) {
      let j = Math.floor(Math.random()*i);
      [array[i-1], array[j]] = [array[j], array[i-1]];
    }
  }

  let offset = 0;

  function scrollWords() {
    const active = document.querySelector('.active');

    if (active.offsetTop -offset > active.offsetHeight) {
      offset += active.offsetHeight;
      wordsContainer.style.top =`-${offset}px`;
    }
  }

  inputs.addEventListener('keyup', onFlyCheck);
  inputs.addEventListener('keyup', commitWord);
  inputs.addEventListener('keypress', startTime);

  shuffle(wordsArray);

  displayWords(wordsArray);
  addActive();
});
